    @Override
    public void submitRegistration(String loginType,String userId,String name,String gender,String bornDate,String email, String password,String province, String city,String address,String phone,String usertType ) {
        if (loginType.equals("FACEBOOK")||loginType.equals("GMAIL"))
        {
            view.showProgressBar("Update Profile..");
            view.registrationResult(userId,true);
        }else {
            view.showProgressBar("sedang mendaftar..");
            firebaseAuth.createUserWithEmailAndPassword(email, password)
                    .addOnCompleteListener((Activity) context, new OnCompleteListener<AuthResult>() {
                        @Override
                        public void onComplete(@NonNull Task<AuthResult> task) {
                            if (!task.isSuccessful()) {
                                view.registrationResult(task.getException().getMessage(), false);
                            } else {
                                view.registrationResult(task.getResult().getUser().getUid(), true);
                            }
                        }
                    });
        }

    }


    @Override
    public void submitUserDetail(final User user) {
        Log.d("userId update : ",user.getId());
        DatabaseReference childRef = dbRef.child(user.getId());
        childRef.setValue(user);
        childRef.addValueEventListener(new ValueEventListener() {
            @Override
            public void onDataChange(DataSnapshot dataSnapshot) {
                view.detailRegistrationResult(true);
            }
            @Override
            public void onCancelled(DatabaseError databaseError) {
                view.detailRegistrationResult(false);
            }
        });
    }

    @Override
    public void submitUserDetail(final User user, final File[] fileArr) {
        Log.d("userId update : ",user.getId());
        DatabaseReference childRef = dbRef.child(user.getId());
        childRef.setValue(user);
        childRef.addValueEventListener(new ValueEventListener() {
            @Override
            public void onDataChange(DataSnapshot dataSnapshot) {
                if(user.getUsertype().equals("CONSULTANT"))
                    submitConsultantFiles(user, fileArr);
                else
                    view.detailRegistrationResult(true);
            }
            @Override
            public void onCancelled(DatabaseError databaseError) {
                view.detailRegistrationResult(false);
            }
        });
    }

    public void submitConsultantFiles(User user, File[] fileArr) {

        FirebaseStorage storage = FirebaseStorage.getInstance();
        StorageReference storageReference = storage.getReferenceFromUrl("gs://tanyahukum-9d16f.appspot.com");
        StorageReference fileRef = null;
        Bitmap bmp = null;

        for(int x=0;x<4;x++){
            if(x==0) {
                fileRef = storageReference.child("consultant_file/idcard/"+user.getId().toString()+".jpg");
            }
            else if(x==1){
                fileRef = storageReference.child("consultant_file/universitycertificate/"+user.getId().toString()+".jpg");
            }
            else if(x==2){
                fileRef = storageReference.child("consultant_file/profesionalcertificate/"+user.getId().toString()+".jpg");
            }
            else if(x==3){
                fileRef = storageReference.child("consultant_file/bankaccount/"+user.getId().toString()+".jpg");
            }

            bmp = BitmapFactory.decodeFile(fileArr[x].getAbsolutePath());
            bmp = checkOrientation(bmp, fileArr[x]);

            ByteArrayOutputStream byteArrayOutputStream = new ByteArrayOutputStream();
            bmp.compress(Bitmap.CompressFormat.JPEG, 100, byteArrayOutputStream);
            byte[] data = byteArrayOutputStream.toByteArray();

            UploadTask uploadTask = fileRef.putBytes(data);
            uploadTask.addOnFailureListener(new OnFailureListener() {
                @Override
                public void onFailure(@NonNull Exception e) {
                    view.detailRegistrationResult(false);
                }
            }).addOnSuccessListener(new OnSuccessListener<UploadTask.TaskSnapshot>() {
                @Override
                public void onSuccess(UploadTask.TaskSnapshot taskSnapshot) {

                }
            });

        }

        view.detailRegistrationResult(true);

    }

    public Bitmap checkOrientation(Bitmap bmp, File file){
        try {
            ExifInterface exif = new ExifInterface(file.getAbsolutePath());
            int orientation = exif.getAttributeInt(ExifInterface.TAG_ORIENTATION, 1);
            Log.d("EXIF", "Exif: " + orientation);
            Matrix matrix = new Matrix();
            if (orientation == 6) {
                matrix.postRotate(90);
            }
            else if (orientation == 3) {
                matrix.postRotate(180);
            }
            else if (orientation == 8) {
                matrix.postRotate(270);
            }
            // rotating bitmap
            bmp = Bitmap.createBitmap(bmp, 0, 0, bmp.getWidth(), bmp.getHeight(), matrix, true);
        }
        catch (Exception e) {

        }

        return bmp;
    }

    @Override
    public void getProvince() {
        Log.d("masuk","masuk");
        Query userQuery;
            userQuery = countryRef.child("provincies");
        userQuery.addListenerForSingleValueEvent(new ValueEventListener() {
            @Override
            public void onDataChange(DataSnapshot dataSnapshot) {
               Log.d("data consult : ", dataSnapshot.toString());
                List<Province> provinceList=new ArrayList<>();
                Province province=null;
                for (DataSnapshot singleSnapshot : dataSnapshot.getChildren()) {
                    Log.d("single snapshot: ",singleSnapshot.toString());
                    for (DataSnapshot data : singleSnapshot.getChildren()) {
                        province = singleSnapshot.getValue(Province.class);
                    }
                    provinceList.add(province);
                }
                view.showSpinnerProvince(provinceList);
                provinceList.clear();


            }
            @Override
            public void onCancelled(DatabaseError databaseError) {

            }
        });

    }

    @Override
    public void getRegencies(final Long provinceId, final String tipe) {
        Query userQuery;
        userQuery = countryRef.child("Regencies");
        userQuery.addListenerForSingleValueEvent(new ValueEventListener() {
            @Override
            public void onDataChange(DataSnapshot dataSnapshot) {
                Log.d("prov id : ",String.valueOf(provinceId));
                Log.d("data consult : ", dataSnapshot.toString());
                List<Regency> regencyList=new ArrayList<>();
                Regency regency=null;
                for (DataSnapshot singleSnapshot : dataSnapshot.getChildren()) {
                    Log.d("single snapshot: ",singleSnapshot.toString());
                    for (DataSnapshot data : singleSnapshot.getChildren()) {
                        Log.d("data snapshot: ",data.getValue().toString());
                        regency = singleSnapshot.getValue(Regency.class);
                        Log.d("nama prov : ",String.valueOf(regency.getProv_id()));

                    }
                    if (regency.getProv_id()==provinceId) {
                        regencyList.add(regency);
                    }
                }
                view.showSpinnerRegency(regencyList,tipe);


            }
            @Override
            public void onCancelled(DatabaseError databaseError) {

            }
        });

    }
